CREATE TABLE "aparcament" ("idaparcament" serial4 NOT NULL,"nom" varchar(45) NOT NULL,"direccio" varchar(45) NOT NULL,"telefon" int4 DEFAULT NULL,"email" varchar(45) DEFAULT NULL,"admin" varchar(9) NOT NULL,"zona" varchar(45) NOT NULL,"latitude" decimal(20,14) NOT NULL,"longitude" decimal(20,14) NOT NULL,PRIMARY KEY ("idaparcament") );CREATE UNIQUE INDEX "Aparcament_UNIQUE" ON "aparcament" ("nom", "direccio", "latitude", "longitude");CREATE INDEX "Aparcament_Admin_idx" ON "aparcament" ("admin");CREATE INDEX "FK_Aparcament_Zona_idx" ON "aparcament" ("zona");COMMENT ON TABLE "aparcament" IS '		';CREATE TABLE "placa" ("idplaca" serial4 NOT NULL,"idaparcament" int4 NOT NULL,"planta" int4 NOT NULL,"numero" int4 NOT NULL,"tipusplaca" varchar(45) NOT NULL,"activa" bool NOT NULL,PRIMARY KEY ("idplaca") );CREATE UNIQUE INDEX "IdPlaca_UNIQUE" ON "placa" ("idaparcament", "planta", "numero");CREATE INDEX "IdPlaca_Aparcament_idx" ON "placa" ("idaparcament");CREATE INDEX "IdTipusPlaca_idx" ON "placa" ("tipusplaca");CREATE TABLE "poblacio" ("nom" varchar(45) NOT NULL,"provincia" varchar(45) NOT NULL,PRIMARY KEY ("nom", "provincia") );CREATE UNIQUE INDEX "IdPoblacio_UNIQUE" ON "poblacio" ("nom");CREATE INDEX "Provincia_idx" ON "poblacio" ("provincia");CREATE TABLE "provincia" ("nom" varchar(45) NOT NULL,PRIMARY KEY ("nom") );CREATE UNIQUE INDEX "IdProvincia_UNIQUE" ON "provincia" ("nom");CREATE TABLE "reserva" ("idreserva" serial4,"idplaca" int4 NOT NULL,"dni" varchar(9) NOT NULL,"matricula" varchar(10) NOT NULL,"hora_inici" timestamp NOT NULL,"hora_fi" timestamp NOT NULL,"utilitzada" bool,"hora_inici_real" timestamp DEFAULT NULL,"hora_fi_real" timestamp DEFAULT NULL,"preu" numeric(2) DEFAULT NULL,"passada" bool DEFAULT NULL,"anulada" bool DEFAULT NULL,PRIMARY KEY ("idreserva") );CREATE UNIQUE INDEX "IdReserva_UNIQUE" ON "reserva" ("idplaca", "dni", "hora_inici", "hora_fi");CREATE INDEX "IdUsuari_idx" ON "reserva" ("dni");CREATE INDEX "IdPlaca_idx" ON "reserva" ("idplaca");CREATE TABLE "tipus_placa" ("tipusplaca" varchar(45) NOT NULL,PRIMARY KEY ("tipusplaca") );CREATE UNIQUE INDEX "tipusplaca_UNIQUE" ON "tipus_placa" ("tipusplaca");CREATE TABLE "tipus_usuari" ("tipususuari" varchar(45) NOT NULL,PRIMARY KEY ("tipususuari") );CREATE UNIQUE INDEX "tipususuari_UNIQUE" ON "tipus_usuari" ("tipususuari");CREATE TABLE "usuari" ("dni" varchar(9) NOT NULL,"nom" varchar(45) NOT NULL,"cognom1" varchar(45) NOT NULL,"cognom2" varchar(45) NOT NULL,"direccio" varchar(45) NOT NULL,"poblacio" varchar(45) NOT NULL,"codipostal" varchar(5) NOT NULL,"telefon" int4 NOT NULL,"email" varchar(45) NOT NULL,"nomusuari" varchar(15) NOT NULL,"password" varchar(256) NOT NULL,"tipususuari" varchar(45) NOT NULL,"dadesfacturacio" varchar(31) NOT NULL,"matricula" varchar(10) NOT NULL,PRIMARY KEY ("dni") );CREATE UNIQUE INDEX "IdUsuari_UNIQUE" ON "usuari" ("dni");CREATE INDEX "Usuari_Poblacio_idx" ON "usuari" ("poblacio");CREATE INDEX "IdTipusUsuari_idx" ON "usuari" ("tipususuari");CREATE TABLE "zona" ("nom" varchar(45) NOT NULL,PRIMARY KEY ("nom") );CREATE UNIQUE INDEX "IdZona_UNIQUE" ON "zona" ("nom");ALTER TABLE "aparcament" ADD CONSTRAINT "FK_Aparcament_Usuari" FOREIGN KEY ("admin") REFERENCES "usuari" ("dni");ALTER TABLE "placa" ADD CONSTRAINT "FK_Placa_Aparcament" FOREIGN KEY ("idaparcament") REFERENCES "aparcament" ("idaparcament");ALTER TABLE "placa" ADD CONSTRAINT "FK_Placa_Tipus_Placa" FOREIGN KEY ("tipusplaca") REFERENCES "tipus_placa" ("tipusplaca");ALTER TABLE "poblacio" ADD CONSTRAINT "FK_Poblacio_Provincia" FOREIGN KEY ("provincia") REFERENCES "provincia" ("nom");ALTER TABLE "reserva" ADD CONSTRAINT "FK_Reserva_NumPlaca" FOREIGN KEY ("idplaca") REFERENCES "placa" ("idplaca");ALTER TABLE "reserva" ADD CONSTRAINT "FK_Reserva_Usuari" F